      setNames(
        dimSums(
          pm_IndstCO2Captured[,,c("sesobio", "seliqbio", "segabio")][,,'cement'],
          dim = 3)
        * GtC_2_MtCO2,
        'Carbon Management|Carbon Capture|Industry Energy|Cement|+|Biomass (Mt CO2/yr)'
      ),

      setNames(
        dimSums(
          pm_IndstCO2Captured[,,c("sesobio", "seliqbio", "segabio")][,,'chemicals'],
