        # if no solids/liquids/gases in subsector -> NaN, set share to zero
        BioShare.FE.Indst[is.nan(BioShare.FE.Indst)] <- 0
        SynShare.FE.Indst[is.nan(SynShare.FE.Indst)] <- 0
        FosShare.FE.Indst[is.nan(FosShare.FE.Indst)] <- 0

